# app/models/customer.py
from app.api import db
from datetime import datetime

class Customer(db.Model):
    __tablename__ = 'customers'

    id = db.Column(db.Integer, primary_key=True)
    name = db.Column(db.String(100), nullable=False)
    email = db.Column(db.String(120), unique=True, nullable=False)
    password = db.Column(db.String(128), nullable=False)
    created_at = db.Column(db.DateTime, default=datetime.utcnow)

    # Relationship with API keys
    api_keys = db.relationship('CustomerAPIKey', backref='customer', lazy=True)

    def to_dict(self):
        return {
            'id': self.id,
            'name': self.name,
            'email': self.email,
            'created_at': self.created_at,
            'api_keys': [api_key.to_dict() for api_key in self.api_keys]  # Include API keys in response
        }

    def __repr__(self):
        return f'<Customer {self.name} - {self.api_keys}>'
