# app/models/customer_api_key.py
from app.api import db
from datetime import datetime
import secrets

class CustomerAPIKey(db.Model):
    __tablename__ = 'customer_api_keys'

    id = db.Column(db.Integer, primary_key=True)
    customer_id = db.Column(db.Integer, db.ForeignKey('customers.id'), nullable=False)  # Relationship with customer
    api_key = db.Column(db.String(128), unique=True, nullable=False)
    permissions = db.Column(db.String(100), default='read-only')  # e.g., 'read-only', 'full-access'
    created_at = db.Column(db.DateTime, default=datetime.utcnow)
    is_active = db.Column(db.Boolean, default=True)

   
    # Generate a secure API key for the customer
    def generate_key(self):
        self.api_key = secrets.token_urlsafe(32)
        db.session.add(self)
        db.session.commit()

    def to_dict(self):
        return {
            'id': self.id,
            'customer_id': self.customer_id,
            'api_key': self.api_key,
            'permissions': self.permissions,
            'created_at': self.created_at,
            'is_active': self.is_active
        }
