# app/models/integration.py
from app.api import db
from datetime import datetime

class Integration(db.Model):
    __tablename__ = 'integrations'

    id = db.Column(db.Integer, primary_key=True)
    service_name = db.Column(db.String(100), nullable=False)  # e.g., 'Salesforce', 'Stripe'
    api_key = db.Column(db.String(255), nullable=False)
    status = db.Column(db.String(50), default='active')  # active, inactive, etc.
    created_at = db.Column(db.DateTime, default=datetime.utcnow)

    def to_dict(self):
        return {
            'id': self.id,
            'service_name': self.service_name,
            'status': self.status,
            'created_at': self.created_at
        }

# To log integration events
class IntegrationEvent(db.Model):
    __tablename__ = 'integration_events'

    id = db.Column(db.Integer, primary_key=True)
    integration_id = db.Column(db.Integer, db.ForeignKey('integrations.id'), nullable=False)
    event_type = db.Column(db.String(100))  # e.g., 'api_call', 'error', 'sync'
    details = db.Column(db.Text)  # API response or error details
    created_at = db.Column(db.DateTime, default=datetime.utcnow)

    def to_dict(self):
        return {
            'id': self.id,
            'integration_id': self.integration_id,
            'event_type': self.event_type,
            'details': self.details,
            'created_at': self.created_at
        }
