# app/models/service_usage.py
from app.api import db
from datetime import datetime

class ServiceUsage(db.Model):
    __tablename__ = 'service_usages'
    
    id = db.Column(db.Integer, primary_key=True)
    customer_id = db.Column(db.Integer, db.ForeignKey('customers.id'), nullable=False)
    service_id = db.Column(db.Integer, db.ForeignKey('services.id'), nullable=False)
    usage_start = db.Column(db.DateTime, default=datetime.utcnow)
    usage_end = db.Column(db.DateTime)
    duration = db.Column(db.Integer)  # In seconds

    def to_dict(self):
        return {
            'id': self.id,
            'customer_id': self.customer_id,
            'service_id': self.service_id,
            'usage_start': self.usage_start,
            'usage_end': self.usage_end,
            'duration': self.duration
        }
