# app/routes/analytics_routes.py
from flask import Blueprint
from app.api.controllers.analytics_controller import get_service_usage, get_customer_activity, get_operational_metrics

analytics_bp = Blueprint('analytics', __name__)

analytics_bp.route('/service-usage', methods=['GET'])(get_service_usage)
analytics_bp.route('/customer-activity', methods=['GET'])(get_customer_activity)
analytics_bp.route('/operational-metrics', methods=['GET'])(get_operational_metrics)
