# app/routes/customer_api_key_routes.py
from flask import Blueprint
from app.api.controllers.customer_api_key_controller import generate_customer_api_key, protected_customer_endpoint

customer_api_key_bp = Blueprint('customer_api_key', __name__)

# Generate a new API key for a specific customer
customer_api_key_bp.route('/generate/<int:customer_id>', methods=['POST'])(generate_customer_api_key)

# Example protected endpoint using API key validation
customer_api_key_bp.route('/protected', methods=['GET'])(protected_customer_endpoint)
