# app/routes/customer_routes.py
from flask import Blueprint
from app.api.controllers.customer_controller import register_customer, customer_login, view_customer_profile, generate_customer_api_key, deactivate_customer_api_key

customer_bp = Blueprint('customer', __name__)

# Register and login routes
customer_bp.route('/register', methods=['POST'])(register_customer)
customer_bp.route('/login', methods=['POST'])(customer_login)

# Profile and API key management
customer_bp.route('/profile', methods=['GET'])(view_customer_profile)
customer_bp.route('/api_key/generate', methods=['POST'])(generate_customer_api_key)
customer_bp.route('/api_key/deactivate/<int:api_key_id>', methods=['POST'])(deactivate_customer_api_key)
