# app/routes/employee_routes.py
from flask import Blueprint
from app.api.controllers.employee_controller import (
    create_employee, 
    get_all_employees, 
    get_employee_by_id, 
    update_employee, 
    delete_employee
)

employee_bp = Blueprint('employees', __name__)

# Define routes for employee operations
employee_bp.route('/', methods=['POST'])(create_employee)
employee_bp.route('/', methods=['GET'])(get_all_employees)
employee_bp.route('/<int:employee_id>', methods=['GET'])(get_employee_by_id)
employee_bp.route('/<int:employee_id>', methods=['PUT'])(update_employee)
employee_bp.route('/<int:employee_id>', methods=['DELETE'])(delete_employee)