# app/routes/integration_routes.py
from flask import Blueprint
from app.api.controllers.integration_controller import add_integration, get_integrations, call_third_party_service

integration_bp = Blueprint('integration', __name__)

integration_bp.route('/add', methods=['POST'])(add_integration)
integration_bp.route('/list', methods=['GET'])(get_integrations)
integration_bp.route('/call/<string:service_name>', methods=['GET'])(call_third_party_service)
