# app/routes/invoice_routes.py
from flask import Blueprint
from app.api.controllers.invoice_controller import (
    create_invoice, get_all_invoices, get_invoice_by_id, update_invoice, delete_invoice
)

invoice_bp = Blueprint('invoices', __name__)

invoice_bp.route('/', methods=['POST'])(create_invoice)
invoice_bp.route('/', methods=['GET'])(get_all_invoices)
invoice_bp.route('/<int:invoice_id>', methods=['GET'])(get_invoice_by_id)
invoice_bp.route('/<int:invoice_id>', methods=['PUT'])(update_invoice)
invoice_bp.route('/<int:invoice_id>', methods=['DELETE'])(delete_invoice)
