# app/routes/notification_routes.py
from flask import Blueprint
from app.api.controllers.notification_controller import (
    create_notification,
    get_all_notifications,
    get_notification_by_id,
    update_notification,
    delete_notification,
    send_notification
)

notification_bp = Blueprint('notifications', __name__)

# Define routes for the Notification module
notification_bp.route('/', methods=['POST'])(create_notification)
notification_bp.route('/', methods=['GET'])(get_all_notifications)
notification_bp.route('/<int:notification_id>', methods=['GET'])(get_notification_by_id)
notification_bp.route('/<int:notification_id>', methods=['PUT'])(update_notification)
notification_bp.route('/<int:notification_id>', methods=['DELETE'])(delete_notification)
notification_bp.route('/<int:notification_id>/send', methods=['POST'])(send_notification)
