# app/routes/service_routes.py
from flask import Blueprint
from app.api.controllers.service_controller import (
    create_service, get_all_services, get_service_by_id, update_service, delete_service
)

service_bp = Blueprint('services', __name__)

service_bp.route('/', methods=['POST'])(create_service)
service_bp.route('/', methods=['GET'])(get_all_services)
service_bp.route('/<int:service_id>', methods=['GET'])(get_service_by_id)
service_bp.route('/<int:service_id>', methods=['PUT'])(update_service)
service_bp.route('/<int:service_id>', methods=['DELETE'])(delete_service)
