# app/routes/ticket_routes.py
from flask import Blueprint
from app.api.controllers.ticket_controller import (
    create_ticket, get_all_tickets, get_ticket_by_id, update_ticket, delete_ticket, assign_employee_to_ticket
)

ticket_bp = Blueprint('tickets', __name__)

ticket_bp.route('/', methods=['POST'])(create_ticket)
ticket_bp.route('/', methods=['GET'])(get_all_tickets)
ticket_bp.route('/<int:ticket_id>', methods=['GET'])(get_ticket_by_id)
ticket_bp.route('/<int:ticket_id>', methods=['PUT'])(update_ticket)
ticket_bp.route('/<int:ticket_id>', methods=['DELETE'])(delete_ticket)
ticket_bp.route('/<int:ticket_id>/assign/<int:employee_id>', methods=['POST'])(assign_employee_to_ticket)
