from flask import Flask
from app.api import db  # Assuming you are importing the db instance from your api folder
from config import Config



def create_web_app():
    web_app = Flask(__name__)
     # Load the configuration from the config.py file
    web_app.config.from_object(Config)
    
    # Initialize other components
    db.init_app(web_app)

    # Register blueprints
    
    from app.web.routes.customer_routes import web_customer_bp
    web_app.register_blueprint(web_customer_bp, url_prefix='/')
    from app.web.routes.customer_routes import base_bp
    web_app.register_blueprint(base_bp, url_prefix='/')
    from app.web.routes.ticket_routes import ticket_bp
    web_app.register_blueprint(ticket_bp, url_prefix='/')
   
    return web_app